package uk.gov.di.ipv.cri.drivingpermit.api.service.configuration;

import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.extension.ExtendWith;
import org.mockito.Mock;
import org.mockito.junit.jupiter.MockitoExtension;

import java.security.NoSuchAlgorithmException;
import java.security.cert.CertificateException;
import java.security.spec.InvalidKeySpecException;

import static org.junit.jupiter.api.Assertions.assertNotNull;
import static org.mockito.Mockito.when;
import static uk.gov.di.ipv.cri.drivingpermit.library.config.ParameterStoreParameters.*;

@ExtendWith(MockitoExtension.class)
class ConfigurationServiceTest {
    private static final String SPECIFIC_KEY_FORMAT = "/%s/%s";

    @Mock private ParameterStoreService mockParameterStoreService;

    @Test
    void shouldInitialiseConfigFieldsWhenValidInputProvided()
            throws CertificateException, NoSuchAlgorithmException, InvalidKeySpecException {
        String env = "null";
        String paramPath = "/" + env + "/";

        mockFeatureToggles(env);
        mockPrivateParameters(env);
        mockDcsConfig(paramPath);
        mockDvaConfig(paramPath);

        ConfigurationService configurationService =
                new ConfigurationService(mockParameterStoreService);

        assertNotNull(configurationService);
    }

    private void mockPrivateParameters(String env) {

        when(mockParameterStoreService.getParameter("contraindicationMappings"))
                .thenReturn("contraindicationMappings");

        when(mockParameterStoreService.getStackParameter("DocumentCheckResultTableName"))
                .thenReturn("DocumentCheckResultTableName");

        when(mockParameterStoreService.getCommonParameterName("SessionTtl"))
                .thenReturn("600"); // documentCheckItemTtl
    }

    private void mockFeatureToggles(String env) {
        when(mockParameterStoreService.getParameter("dvaDirectEnabled"))
                .thenReturn("dvaDirectEnabled");
        when(mockParameterStoreService.getParameter("dvlaDirectEnabled"))
                .thenReturn("dvlaDirectEnabled");

        when(mockParameterStoreService.getParameter("isPerformanceStub"))
                .thenReturn("isPerformanceStub");

        when(mockParameterStoreService.getParameter("logDcsResponse")).thenReturn("logDcsResponse");
    }

    private void mockDcsConfig(String paramPath) {

        when(mockParameterStoreService.getParameter(DCS_ENDPOINT)).thenReturn("dcsEndpoint");

        // TLS
        when(mockParameterStoreService.getParameter(DCS_HTTPCLIENT_TLS_CERT))
                .thenReturn(
                        "");

        when(mockParameterStoreService.getEncryptedParameter(DCS_HTTPCLIENT_TLS_KEY))
                .thenReturn(
                        "");

        when(mockParameterStoreService.getParameter(DCS_HTTPCLIENT_TLS_ROOT_CERT))
                .thenReturn(
                        "");
        when(mockParameterStoreService.getParameter(DCS_HTTPCLIENT_TLS_INTER_CERT))
                .thenReturn(
                        "");

        // JWE / JWS
        when(mockParameterStoreService.getParameter(DCS_DRIVING_PERMIT_CRI_SIGNING_CERT))
                .thenReturn(
                        "");
        when(mockParameterStoreService.getEncryptedParameter(DCS_DRIVING_PERMIT_CRI_SIGNING_KEY))
                .thenReturn(
                        "");

        when(mockParameterStoreService.getParameter(DCS_ENCRYPTION_CERT))
                .thenReturn(
                        "");

        when(mockParameterStoreService.getParameter(DCS_SIGNING_CERT_THUMB))
                .thenReturn(
                        "");
        when(mockParameterStoreService.getEncryptedParameter(DCS_DRIVING_PERMIT_ENCRYPTION_KEY))
                .thenReturn(
                        "");
    }

    private void mockDvaConfig(String paramPath) {

        when(mockParameterStoreService.getParameter(DVA_ENDPOINT)).thenReturn("DVA/dvaEndpoint");

        when(mockParameterStoreService.getParameter(DVA_USERNAME)).thenReturn("DVA/Username");
        when(mockParameterStoreService.getParameter(DVA_PASSWORD)).thenReturn("DVA/Password");

        // TLS
        when(mockParameterStoreService.getParameter(DVA_HTTPCLIENT_TLS_CERT))
                .thenReturn(
                        "");
        when(mockParameterStoreService.getEncryptedParameter(DVA_HTTPCLIENT_TLS_KEY))
                .thenReturn(
                        "");
        when(mockParameterStoreService.getParameter(DVA_HTTPCLIENT_TLS_ROOT_CERT))
                .thenReturn(
                        "");
        when(mockParameterStoreService.getParameter(DVA_HTTPCLIENT_TLS_INTER_CERT))
                .thenReturn(
                        "");

        // JWE / JWS
        when(mockParameterStoreService.getParameter(DVA_DRIVING_PERMIT_CRI_SIGNING_CERT))
                .thenReturn(
                        "");
        when(mockParameterStoreService.getEncryptedParameter(DVA_DRIVING_PERMIT_CRI_SIGNING_KEY))
                .thenReturn(
                        "");

        when(mockParameterStoreService.getParameter(DVA_ENCRYPTION_CERT))
                .thenReturn(
                        "");
        when(mockParameterStoreService.getParameter(DVA_SIGNING_CERT_THUMB))
                .thenReturn(
                        "");

        when(mockParameterStoreService.getEncryptedParameter(DVA_DRIVING_PERMIT_ENCRYPTION_KEY))
                .thenReturn(
                        "");
    }
}
